import React from 'react'
import { useState} from 'react';
import { UserData } from './UserData';
import { Link } from 'react-router-dom';
export const UserForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    mobileNumber: '',
    dateOfBirth: '',
    email: '',
    password: ''
  });

  const [errors, setErrors] = useState({
    name: '',
    mobileNumber: '',
    dateOfBirth: '',
    email: '',
    password: ''
  });

  const [submitted, setSubmitted] = useState(false); // Track form submission

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Real-time validation
    switch (name) {
      case 'name':
        // Add your name validation logic here
        break;
      case 'mobileNumber':
        const mobileRegex = /^[0-9]{10}$/;
        setErrors({ ...errors, [name]: mobileRegex.test(value) ? '' : 'Invalid mobile number' });
        break;
      case 'dateOfBirth':
        // Add your date validation logic here
        break;
      case 'email':
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        setErrors({ ...errors, [name]: emailRegex.test(value) ? '' : 'Invalid email address' });
        break;
      case 'password':
        const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/;
        setErrors({ ...errors, [name]: passwordRegex.test(value) ? '' : 'Password must be at least 8 characters long and contain at least one uppercase letter, one lowercase letter, and one number' });
        break;
      default:
        break;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    localStorage.setItem('formData', JSON.stringify(formData));

    

    setSubmitted(true);
}

  // Check if all fields are filled correctly
  const isFormValid = () => {
    return (
      formData.name &&
      formData.mobileNumber &&
      formData.dateOfBirth &&
      formData.email &&
      formData.password &&
      Object.values(errors).every((error) => !error)
    );
  };

  return (
    <div>
      {!submitted ? (
        <div className="max-w-md mx-auto mt-8 p-6 bg-gray-100 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold mb-6">Registration Form</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="name" className="block font-semibold">Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="w-full p-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
              {errors.name && <span className="text-red-500">{errors.name}</span>}
            </div>
            <div>
              <label htmlFor="mobileNumber" className="block font-semibold">Mobile Number:</label>
              <input
                type="text"
                id="mobileNumber"
                name="mobileNumber"
                value={formData.mobileNumber}
                onChange={handleChange}
                className="w-full p-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
              {errors.mobileNumber && <span className="text-red-500">{errors.mobileNumber}</span>}
            </div>
            <div>
              <label htmlFor="dateOfBirth" className="block font-semibold">Date of Birth:</label>
              <input
                type="date"
                id="dateOfBirth"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleChange}
                className="w-full p-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
              {errors.dateOfBirth && <span className="text-red-500">{errors.dateOfBirth}</span>}
            </div>
            <div>
              <label htmlFor="email" className="block font-semibold">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full p-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
              {errors.email && <span className="text-red-500">{errors.email}</span>}
            </div>
            <div>
              <label htmlFor="password" className="block font-semibold">Password:</label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="w-full p-2 border rounded-md focus:outline-none focus:border-blue-500"
                required
              />
              {errors.password && <span className="text-red-500">{errors.password}</span>}
            </div>
            <Link to="/userData" className={`w-full   bg-blue-500 text-white py-2 rounded-md ${isFormValid() ? 'hover:bg-blue-600 transition duration-300' : 'opacity-50 cursor-not-allowed'}`} disabled={!isFormValid()} onClick={handleSubmit}>Submit</Link>
          </form>
        </div>
      ) : (
        <UserData formData={formData} />
      )}
    </div>
  );
  
}
