import React from 'react'
import { useState } from 'react';

export const UserData = ({formData}) => {
  const [image, setImage] = useState(formData.avatar || '');

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = () => {
      if (reader.readyState === 2) {
        setImage(reader.result);
      }
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md">
      <div className="p-6 border border-gray-300">
        <div className="flex items-center">
          <div className="flex-shrink-0 mr-6">
            <label htmlFor="profileImage" className="cursor-pointer">
              <img
                src={image || 'https://via.placeholder.com/150'}
                alt="Profile"
                className="rounded-full w-32 h-32 border-4 border-blue-500"
              />
              <input
                type="file"
                id="profileImage"
                accept="image/*"
                className="hidden"
                onChange={handleImageChange}
              />
            </label>
          </div>
          <div>
            <h3 className="text-2xl font-semibold mb-2">{formData.name}</h3>
            <p className="text-gray-600 text-lg mb-2">{formData.email}</p>
            <p className="text-gray-600 mb-2"><span className="font-semibold">Mobile Number:</span> {formData.mobileNumber}</p>
            <p className="text-gray-600 mb-2"><span className="font-semibold">Date of Birth:</span> {formData.dateOfBirth}</p>
          </div>
        </div>
      </div>
    </div>

  );
}
